import {
    FETCH_PRODUCTS,
  } from "./types";

export const fetchProducts = () => (dispatch) => {
    fetch("http://localhost/aura/api/v1/products")
      .then((res) => res.json())
      .then((data) => data.data)
      .catch((err) =>
        fetch("db.json")
          .then((res) => res.json())
          .then((data) => data.data)
      )
      .then((data) => {
        dispatch({ type: FETCH_PRODUCTS, payload: data });
      });
  };
