import React, { Component } from 'react';
import Backendurl from '../api/backendurl.json'



export default class Apidata extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false
        }
    }

/*     componentDidMount() {
        this.setState({ data: data.articles, loading: false}, () => console.log(data.articles))
        console.log('app mounted');
        fetch('http://localhost/aura/api/v1/products')
            .then(data => data.json())
            .then(data => this.setState({ data: data.articles, loading: false}))
            console.log('Results -', JSON.stringify(this.data));
    } */




    componentDidMount() {
        fetch('http://localhost/aura/api/v1/products')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json.data,
                })
            });
            console.log('Results -', JSON.stringify(this.data));
    }


    render() {
        var { isLoaded, items } = this.state;
        if (!isLoaded) {
            return <div>Loading...</div>;
        }
        return (
            <div>

                <ul>
                    {items.map(item => (
                        <li key="{item.id}">
                            {item.name}
                            <img src={`${Backendurl.BACKENDURL}${item.banner}`} className="img-fluid" alt=""/>                 
                        </li> 
                    ))}
                </ul>
            </div>
        );
    }
}
